 // Element in the collection  class Node {  var info: String = &#34;&#34;  init(_ s: String) {  self.info = s  }  }   // Iterator  protocol Iterator {  func hasNext() -&gt; Bool  func getNext() -&gt; Node  }   // Concrete iterator  class PositiveIterator: Iterator {   var collection: Collection  var curIndex = 0   init(_ c: Collection) {  self.collection = c  }   func hasNext() -&gt; Bool {  if curIndex &lt; collection.nodes.count {  return true  } else {  return false  }  }   func getNext() -&gt; Node {  var node = collection.nodes[curIndex]  curIndex += 1  return node  }   }   // Concrete iterator  class RevertIerator: Iterator {  var collection: Collection  var curIndex = 0   init(_ c: Collection) {  self.collection = c  self.curIndex = collection.nodes.count - 1  }   func hasNext() -&gt; Bool {  if curIndex &gt;= 0{  return true  } else {  return false  }  }   func getNext() -&gt; Node {  var node = collection.nodes[curIndex]  curIndex -= 1  return node  }  }   // Collection  protocol Collection {  var nodes: [Node] { get set }  func getIterator() -&gt; Iterator  func getRevertIterator() -&gt; Iterator  }   // Concrete collecton  class NodesCollection: Collection {  var nodes: [Node]  init() {  self.nodes = []  }   func append(_ n: Node) {  nodes.append(n)  }   func getIterator() -&gt; Iterator {  return PositiveIterator(self)  }   func getRevertIterator() -&gt; Iterator {  return RevertIerator(self)  }  }   let str = &#34;hello world&#34;  let collection = NodesCollection()  for s in str {  collection.append(Node(String(s)))  }   var it = collection.getIterator()   while (it.hasNext()) {  var n = it.getNext()  print(n.info, terminator: &#34; &#34;)  }   print()   it = collection.getRevertIterator()   while (it.hasNext()) {  var n = it.getNext()  print(n.info, terminator: &#34;_&#34;)  } Mediator The mediator constrains the interaction between the component, the components can only notify the mediator and the mediator can manage the action of all the components