 // Component basic class  class Component {  var m: Mediator  init(mediator: Mediator) {  self.m = mediator  m.notify(component: self)  }  }   // Concrete component  class Button: Component {  func click() {  m.notify(component: self)  }  }  // Concrete component  class CheckBox: Component {  var check = false  func isCheck() -&gt; Bool {  return check  }  }  // Concrete component  class PasswordText: Component {  var text: String = &#34;&#34;  func getText() -&gt; String{  return text  }   }  // Concrete component  class UserNameText: Component {  var text: String = &#34;&#34;  func getText() -&gt; String{  return text  }   }   protocol Mediator {  func notify(component: Component)  }   // Concrete mediator  class LoginMediator: Mediator {  var btnLogin: Button?  var tickSavePassword: CheckBox?  var password: PasswordText?  var userName: UserNameText?  func notify(component: Component) {  if component is Button {  var btn = component as! Button  if self.btnLogin == nil {  // self btn initialize  self.btnLogin = btn  return  }   if (canLogin(userName: userName?.getText(), password: password?.getText())) {  print(&#34;Login success&#34;)  if let wantSave = tickSavePassword?.isCheck() {  // save the userName and password  }  } else {  print(&#34;Login failed&#34;)  }  } else if component is CheckBox {  var box = component as! CheckBox  self.tickSavePassword = box  } else if component is UserNameText {  var userName = component as! UserNameText  self.userName = userName  } else if component is PasswordText {  var password = component as! PasswordText  self.password = password  }   }  }   // The function check whether the username and password match  func canLogin(userName: String?, password: String?) -&gt; Bool {  if userName == nil || password == nil {  return false  }  if let name = userName, let word = password{  if name == &#34;hello&#34; &amp;&amp; word == &#34;Mike&#34; {  return true  } else {  return false  }  }  return false  }   let mediator = LoginMediator()  let userName = UserNameText(mediator: mediator)  let password = PasswordText(mediator: mediator)  let tickSavePassword = CheckBox(mediator: mediator)  let btnLogin = Button(mediator: mediator)   userName.text = &#34;hello&#34;  password.text = &#34;Mike&#34;  tickSavePassword.check = true   btnLogin.click() Memento It allow to save or recover the object state before without expose the objectâ€™s implemented detail. The originator can create the snapshot of itself and can restore the snapshot when it need. The memento is a value object of the originatorâ€™s snapshot state, itâ€™s built by the initializer and pass the information by only one time. The caretaker can only use the originatorâ€™s makeMemento() and restore().
