 // Originator  protocol Animal {  func getMemento() -&gt; Memento  }   // Memento  protocol Memento {  func restore()  }   // Concrete originator  class Dog: Animal {  private var age: Int  private var weight: Float  private var legCount: Int   init(age: Int, weight: Float, legCount: Int) {  self.age = age  self.weight = weight  self.legCount = legCount  }  func getMemento() -&gt; Memento {  return DogData(dog: self, age: age, weight: weight, legCount: legCount)  }  func setAge(age: Int) {  self.age = age  }  func setWeight(weight: Float) {  self.weight = weight  }  func setLegCount(legCount: Int) {  self.legCount = legCount  }   func info() -&gt; String {  return &#34;age: \(age)weight: \(weight)legCount: \(legCount)&#34;  }  }   // Concrete memento  class DogData: Memento {  private var age: Int  private var weight: Float  private var legCount: Int   private var dog: Dog?   init(dog: Dog, age: Int, weight: Float, legCount: Int) {  self.dog = dog  self.age = age  self.weight = weight  self.legCount = legCount  }   func restore() {  dog?.setAge(age: age)  dog?.setWeight(weight: weight)  dog?.setLegCount(legCount: legCount)  }   }   // CareTaker  class Recored {  var dog: Dog  var history: [Memento] = []  init(dog: Dog) {  self.dog = dog  }  func save() {  var backup = dog.getMemento()  history.append(backup)  if (history.count &gt; 10) {  history.remove(at: 0)  }  }   func undo() {  if history.count &lt;= 0 {  return  }  var data = history[history.count - 1]  data.restore()  history.remove(at: history.count - 1)  }   }   let dog = Dog(age: 2, weight: 15, legCount: 4)  let record = Recored(dog: dog)  record.save()  print(dog.info())   dog.setAge(age: 4)  dog.setWeight(weight: 20)  print(dog.info())   record.undo()  print(dog.info()) Observer The publisher has some subscribers reference, it can add subscriber, remove subscriber, and notify all subscriber.
