 // Abstract state  class State {  var context: Context?  var name: String = &#34;&#34;  init(context: Context?) {  self.context = context  }   }   // Concrete state  class NewState: State {  override init(context: Context?) {  super.init(context: context)  self.name = &#34;New&#34;  }  func start() {  print(&#34;Call start&#34;)  if self.name == &#34;New&#34; {  context?.setState(state: RunnableState(context: context))  } else {  print(&#34;Can&#39;t go to runnable&#34;)  }  }  }   // Concrete state  class RunnableState: State {  override init(context: Context?) {  super.init(context: context)  self.name = &#34;Runnable&#34;  }  func getCpu() {  if self.name == &#34;Runnable&#34; {  context?.setState(state: RunningState(context: context))  } else {  print(&#34;Can&#39;t go to running&#34;)  }  }  }   // Concrete state  class RunningState: State {  override init(context: Context?) {  super.init(context: context)  self.name = &#34;Running&#34;  }   func suspend() {  if self.name == &#34;Running&#34; {  context?.setState(state: BlockState(context: context))  }  }   func stop() {  if self.name == &#34;Running&#34; {  context?.setState(state: DeadState(context: context))  }  }   }   // Concrete state  class BlockState: State {  override init(context: Context?) {  super.init(context: context)  self.name = &#34;Block&#34;  }   func resume() {  if name == &#34;Block&#34; {  context?.setState(state: RunnableState(context: context))  }  }  }   // Concrete state  class DeadState: State {  override init(context: Context?) {  super.init(context: context)  self.name = &#34;Dead&#34;  }    }   // Thread Context  class Context {  private var state: State?  init() {  self.state = NewState(context: self)  }   func setState(state: State) {  self.state = state  }   func getState() -&gt; State? {  return self.state  }   func start() {  if let s = self.state as? NewState {  s.start()  }  }   func suspend() {  if let s = self.state as? RunningState {  s.suspend()  }  }    func resume() {